mapper.select:
SELECT * FROM fund_net_value fnv WHERE NAVLATESTDATE>=#{selectDate} AND fnv.code IN
#{item}
ORDER BY NAVLATESTDATE DESC

mapper.update:
UPDATE fund_net_value
SET ADJUSTEDFACTOR=#{adjustedFactor}
WHERE ID=#{id}

mapper.select:
SELECT MAX(a.minDate) FROM ( SELECT MIN(NAVLATESTDATE) minDate FROM fund_net_value WHERE CODE IN
#{item}
GROUP BY code ) as a

SELECT MIN(a.maxDate) FROM ( SELECT MAX(NAVLATESTDATE) maxDate FROM fund_net_value WHERE CODE IN
#{item}
GROUP BY code ) as a
